import React, { useState, useEffect } from 'react';
import { Search, Filter, TrendingUp, TrendingDown, FileText, Calendar, Star, Eye, Download } from 'lucide-react';
import { apiService, AnalysisResult } from '../services/apiService';
import LoadingSpinner from '../components/UI/LoadingSpinner';
import StockChart from '../components/Charts/StockChart';

// Interface AnalysisResult agora importada do apiService
// Criando um alias para compatibilidade
type Analysis = AnalysisResult & {
  title?: string;
  type?: 'technical' | 'fundamental' | 'quantitative' | 'sentiment';
  createdAt?: string;
  summary?: string;
  content?: string;
  author?: string;
  views?: number;
  rating?: number;
  tags?: string[];
  targetPrice?: number;
  stopLoss?: number;
};

const Analytics: React.FC = () => {
  const [analyses, setAnalyses] = useState<Analysis[]>([]);
  const [filteredAnalyses, setFilteredAnalyses] = useState<Analysis[]>([]);
  const [selectedAnalysis, setSelectedAnalysis] = useState<Analysis | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedType, setSelectedType] = useState<string>('all');
  const [selectedRecommendation, setSelectedRecommendation] = useState<string>('all');
  const [sortBy, setSortBy] = useState<'date' | 'confidence' | 'rating'>('date');

  useEffect(() => {
    loadAnalyses();
  }, []);

  useEffect(() => {
    filterAnalyses();
  }, [analyses, searchTerm, selectedType, selectedRecommendation, sortBy]);

  const loadAnalyses = async () => {
    try {
      setIsLoading(true);
      const data = await apiService.getAnalyses();
      setAnalyses(data);
      setError(null);
    } catch (err) {
      console.error('Erro ao carregar anÃ¡lises:', err);
      setError('Erro ao carregar dados');
      // Dados mock para desenvolvimento
      const mockAnalyses: Analysis[] = [
        {
          id: '1',
          title: 'AnÃ¡lise TÃ©cnica PETR4 - Rompimento de ResistÃªncia Importante',
          symbol: 'PETR4',
          type: 'technical',
          recommendation: 'buy',
          confidence: 85,
          createdAt: '2024-01-15T10:30:00Z',
          summary: 'PETR4 rompeu importante resistÃªncia em R$ 30,00 com volume expressivo. PrÃ³ximo alvo em R$ 35,00.',
          content: 'AnÃ¡lise detalhada da aÃ§Ã£o PETR4 mostra rompimento de resistÃªncia tÃ©cnica importante...',
          author: 'Agente TÃ©cnico',
          views: 1247,
          rating: 4.5,
          tags: ['petrÃ³leo', 'energia', 'rompimento', 'alta'],
          targetPrice: 35.00,
          stopLoss: 28.50,
        },
        {
          id: '2',
          title: 'AnÃ¡lise Fundamentalista VALE3 - Resultados Q4 e Perspectivas',
          symbol: 'VALE3',
          type: 'fundamental',
          recommendation: 'hold',
          confidence: 72,
          createdAt: '2024-01-15T09:15:00Z',
          summary: 'Resultados do Q4 dentro do esperado. PreÃ§o do minÃ©rio de ferro permanece volÃ¡til.',
          content: 'A Vale apresentou resultados do Q4 alinhados com as expectativas do mercado...',
          author: 'Agente Fundamentalista',
          views: 892,
          rating: 4.2,
          tags: ['mineraÃ§Ã£o', 'commodities', 'resultados', 'dividendos'],
          targetPrice: 75.00,
        },
        {
          id: '3',
          title: 'Modelo Quantitativo ITUB4 - Oportunidade de Valor Identificada',
          symbol: 'ITUB4',
          type: 'quantitative',
          recommendation: 'buy',
          confidence: 78,
          createdAt: '2024-01-15T08:45:00Z',
          summary: 'Modelo indica subavaliaÃ§Ã£o de 15%. P/VPA atrativo comparado aos pares.',
          content: 'Nosso modelo quantitativo identificou uma oportunidade de valor no ITUB4...',
          author: 'Agente Quantitativo',
          views: 634,
          rating: 4.0,
          tags: ['bancos', 'valor', 'p/vpa', 'subavaliado'],
          targetPrice: 32.00,
          stopLoss: 26.00,
        },
        {
          id: '4',
          title: 'AnÃ¡lise de Sentimento MGLU3 - PressÃ£o Vendedora Continua',
          symbol: 'MGLU3',
          type: 'sentiment',
          recommendation: 'sell',
          confidence: 68,
          createdAt: '2024-01-15T07:20:00Z',
          summary: 'Sentimento negativo persiste. NotÃ­cias sobre reestruturaÃ§Ã£o geram incerteza.',
          content: 'A anÃ¡lise de sentimento do mercado em relaÃ§Ã£o ao MGLU3 continua negativa...',
          author: 'Agente de Sentimento',
          views: 456,
          rating: 3.8,
          tags: ['varejo', 'reestruturaÃ§Ã£o', 'sentimento', 'negativo'],
          targetPrice: 6.50,
          stopLoss: 9.20,
        },
      ];
      setAnalyses(mockAnalyses);
    } finally {
      setIsLoading(false);
    }
  };

  const filterAnalyses = () => {
    let filtered = [...analyses];

    // Filtro por termo de busca
    if (searchTerm) {
      filtered = filtered.filter(
        (analysis) =>
          analysis.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          analysis.symbol.toLowerCase().includes(searchTerm.toLowerCase()) ||
          analysis.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Filtro por tipo
    if (selectedType !== 'all') {
      filtered = filtered.filter((analysis) => analysis.type === selectedType);
    }

    // Filtro por recomendaÃ§Ã£o
    if (selectedRecommendation !== 'all') {
      filtered = filtered.filter((analysis) => analysis.recommendation === selectedRecommendation);
    }

    // OrdenaÃ§Ã£o
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'date':
          return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
        case 'confidence':
          return b.confidence - a.confidence;
        case 'rating':
          return b.rating - a.rating;
        default:
          return 0;
      }
    });

    setFilteredAnalyses(filtered);
  };

  const getTypeIcon = (type: Analysis['type']) => {
    switch (type) {
      case 'technical':
        return <TrendingUp className="w-4 h-4" />;
      case 'fundamental':
        return <FileText className="w-4 h-4" />;
      case 'quantitative':
        return <TrendingDown className="w-4 h-4" />;
      case 'sentiment':
        return 'ðŸ’­';
      default:
        return <FileText className="w-4 h-4" />;
    }
  };

  const getTypeLabel = (type: Analysis['type']) => {
    switch (type) {
      case 'technical':
        return 'TÃ©cnica';
      case 'fundamental':
        return 'Fundamentalista';
      case 'quantitative':
        return 'Quantitativa';
      case 'sentiment':
        return 'Sentimento';
      default:
        return 'AnÃ¡lise';
    }
  };

  const getRecommendationColor = (recommendation: Analysis['recommendation']) => {
    switch (recommendation) {
      case 'buy':
        return 'text-success-600 bg-success-100';
      case 'sell':
        return 'text-danger-600 bg-danger-100';
      case 'hold':
        return 'text-warning-600 bg-warning-100';
      default:
        return 'text-secondary-600 bg-secondary-100';
    }
  };

  const getRecommendationLabel = (recommendation: Analysis['recommendation']) => {
    switch (recommendation) {
      case 'BUY':
        return 'Compra';
